function _drag_init(e){selected=e,x_elem=x_pos-selected.offsetLeft,y_elem=y_pos-selected.offsetTop+50}function _move_elem(e){x_pos=document.all?window.event.clientX:e.pageX,y_pos=document.all?window.event.clientY:e.pageY,null!==selected&&(selected.style.left=x_pos-x_elem+"px",selected.style.top=y_pos-y_elem+"px")}function _destroy(){selected=null}var selected=null,x_pos=0,y_pos=0,x_elem=0,y_elem=0;document.getElementById("player").onmousedown=function(){return _drag_init(this),!1},document.onmousemove=_move_elem,document.onmouseup=_destroy;