function enemy(n,e,t){this.level=n,this.hp=e,this.speed=t}function spawn(n,e){for(var t=0;t<spawnCollection.length;t++){var o=document.createElement("div");o.className="enemy-unit unit-speed",spawnBox.appendChild(o),n(o)}}function sendUnit(n){for(var e=0;e<spawnCollection.length;e++)setTimeout(function(){n.style.left="100px"},200)}function unitPos(n){setInterval(function(){var e=n.offsetTop,t=n.offsetLeft},1e3)}function calc(){var n=10;n>=counter&&(counter+=1),console.log("Count: "+counter)}for(var spawnBox=document.getElementById("cell-202"),pop=10,spawnCollection=[],i=0;pop>i;i++)spawnCollection[i]=new enemy(1,100,5);spawn(sendUnit,unitPos);var counter=0;setInterval(function(){calc()},500);